import React, { useEffect, useState } from 'react';

import Login from './components/Login/Login';
import Home from './components/Home/Home';
import MainHeader from './components/MainHeader/MainHeader';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(()=>{
    const storedUserLoggedinfo = localStorage.getItem('key'); // внутри call back функции берем данные с localStorage методом getItem() через ключ "key" и сох
                                                              // Кух useEffect первым параметром принимает call back функцию где внутри этой фукнции опрабатывается Side Effect в нашем случае данные с local storage
    if (storedUserLoggedinfo === '1'){
      setIsLoggedIn(true)
    }
  }, []);
  const loginHandler = (email, password) => {
    // We should of course check email and password
    // But it's just a dummy/ demo anyways
    localStorage.setItem('key', '1')
    setIsLoggedIn(true);

  };

  const logoutHandler = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('key')
  };

  return (
    <React.Fragment>
      <MainHeader isAuthenticated={isLoggedIn} onLogout={logoutHandler} />
      <main>
        {!isLoggedIn && <Login onLogin={loginHandler} />}
        {isLoggedIn && <Home onLogout={logoutHandler} />}
      </main>
    </React.Fragment>
  );
}

export default App;
